contracts/DeviceRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

/**
 * @title DeviceRegistry
 * @dev Registry for IoT devices with ZKP-based authentication
 */
contract DeviceRegistry {
    struct Device {
        address owner;
        bytes32 deviceId;
        bytes32 firmwareHash;
        bytes32 secretHash; // Hash of the secret used for ZKP
        uint256 registrationTime;
        bool isActive;
    }

    // Events
    event DeviceRegistered(
        bytes32 indexed deviceId,
        address indexed owner,
        bytes32 firmwareHash,
        uint256 timestamp
    );

    event DeviceUpdated(
        bytes32 indexed deviceId,
        bytes32 newFirmwareHash,
        uint256 timestamp
    );

    event DeviceDeactivated(
        bytes32 indexed deviceId,
        uint256 timestamp
    );

    // State variables
    mapping(bytes32 => Device) public devices;
    mapping(address => bytes32[]) public ownerDevices;
    bytes32[] public allDeviceIds;

    uint256 public totalDevices;
    address public admin;

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    modifier onlyDeviceOwner(bytes32 deviceId) {
        require(devices[deviceId].owner == msg.sender, "Only device owner can perform this action");
        _;
    }

    modifier deviceExists(bytes32 deviceId) {
        require(devices[deviceId].owner != address(0), "Device does not exist");
        _;
    }

    constructor() {
        admin = msg.sender;
    }

    /**
     * @dev Register a new IoT device
     * @param deviceId Unique identifier for the device
     * @param firmwareHash Hash of the device firmware
     * @param secretHash Hash of the secret used for ZKP authentication
     */
    function registerDevice(
        bytes32 deviceId,
        bytes32 firmwareHash,
        bytes32 secretHash
    ) external {
        require(devices[deviceId].owner == address(0), "Device already registered");
        require(deviceId != bytes32(0), "Invalid device ID");
        require(firmwareHash != bytes32(0), "Invalid firmware hash");
        require(secretHash != bytes32(0), "Invalid secret hash");

        Device memory newDevice = Device({
            owner: msg.sender,
            deviceId: deviceId,
            firmwareHash: firmwareHash,
            secretHash: secretHash,
            registrationTime: block.timestamp,
            isActive: true
        });

        devices[deviceId] = newDevice;
        ownerDevices[msg.sender].push(deviceId);
        allDeviceIds.push(deviceId);
        totalDevices++;

        emit DeviceRegistered(deviceId, msg.sender, firmwareHash, block.timestamp);
    }

    /**
     * @dev Update device firmware hash (only by owner)
     * @param deviceId Device identifier
     * @param newFirmwareHash New firmware hash
     */
    function updateFirmware(
        bytes32 deviceId,
        bytes32 newFirmwareHash
    ) external deviceExists(deviceId) onlyDeviceOwner(deviceId) {
        require(newFirmwareHash != bytes32(0), "Invalid firmware hash");
        require(devices[deviceId].isActive, "Device is not active");

        devices[deviceId].firmwareHash = newFirmwareHash;

        emit DeviceUpdated(deviceId, newFirmwareHash, block.timestamp);
    }

    /**
     * @dev Deactivate a device (only by owner or admin)
     * @param deviceId Device identifier
     */
    function deactivateDevice(bytes32 deviceId) external deviceExists(deviceId) {
        require(
            devices[deviceId].owner == msg.sender || msg.sender == admin,
            "Only device owner or admin can deactivate"
        );
        require(devices[deviceId].isActive, "Device already inactive");

        devices[deviceId].isActive = false;

        emit DeviceDeactivated(deviceId, block.timestamp);
    }

    /**
     * @dev Get device information
     * @param deviceId Device identifier
     * @return Device struct
     */
    function getDevice(bytes32 deviceId) external view returns (Device memory) {
        require(devices[deviceId].owner != address(0), "Device does not exist");
        return devices[deviceId];
    }

    /**
     * @dev Get devices owned by an address
     * @param owner Owner address
     * @return Array of device IDs
     */
    function getDevicesByOwner(address owner) external view returns (bytes32[] memory) {
        return ownerDevices[owner];
    }

    /**
     * @dev Get all registered device IDs (paginated)
     * @param offset Starting index
     * @param limit Maximum number of results
     * @return Array of device IDs
     */
    function getAllDevices(uint256 offset, uint256 limit) external view returns (bytes32[] memory) {
        require(offset < allDeviceIds.length, "Offset out of bounds");

        uint256 end = offset + limit;
        if (end > allDeviceIds.length) {
            end = allDeviceIds.length;
        }

        bytes32[] memory result = new bytes32[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = allDeviceIds[i];
        }

        return result;
    }

    /**
     * @dev Verify if device is registered and active
     * @param deviceId Device identifier
     * @return bool True if device is registered and active
     */
    function isDeviceActive(bytes32 deviceId) external view returns (bool) {
        return devices[deviceId].owner != address(0) && devices[deviceId].isActive;
    }

    /**
     * @dev Get secret hash for ZKP verification (used by verifier contract)
     * @param deviceId Device identifier
     * @return bytes32 Secret hash
     */
    function getSecretHash(bytes32 deviceId) external view returns (bytes32) {
        require(devices[deviceId].owner != address(0), "Device does not exist");
        require(devices[deviceId].isActive, "Device is not active");
        return devices[deviceId].secretHash;
    }
}

contracts/IntegrityLog.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./DeviceRegistry.sol";

/**
 * @title IntegrityLog
 * @dev Immutable logging of verified IoT data with ZKP authentication
 */
contract IntegrityLog {
    struct LogEntry {
        bytes32 deviceId;
        bytes32 dataHash;        // Hash of the sensor data
        bytes32 proofHash;       // Hash of the ZKP proof
        uint256 timestamp;
        uint256 blockNumber;
        bool verified;           // Whether the ZKP was verified
    }

    struct BatchLog {
        bytes32 merkleRoot;      // Merkle root of batched data
        uint256 batchSize;
        uint256 timestamp;
        string ipfsHash;         // IPFS hash for raw data storage
    }

    // Events
    event DataLogged(
        bytes32 indexed deviceId,
        bytes32 indexed dataHash,
        bytes32 proofHash,
        uint256 timestamp,
        bool verified
    );

    event BatchLogged(
        bytes32 indexed merkleRoot,
        uint256 batchSize,
        string ipfsHash,
        uint256 timestamp
    );

    event AlertTriggered(
        bytes32 indexed deviceId,
        string alertType,
        bytes32 dataHash,
        uint256 timestamp
    );

    // State variables
    DeviceRegistry public deviceRegistry;

    mapping(bytes32 => LogEntry[]) public deviceLogs;  // deviceId => logs
    mapping(bytes32 => uint256) public logCounts;      // deviceId => count

    BatchLog[] public batchLogs;
    mapping(bytes32 => bool) public processedProofs;   // Prevent replay attacks

    uint256 public totalLogs;
    address public admin;
    address public verifierContract;

    // Alert thresholds (can be extended)
    mapping(string => uint256) public alertThresholds;

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    modifier onlyVerifier() {
        require(msg.sender == verifierContract, "Only verifier contract can call this");
        _;
    }

    modifier onlyAuthorized() {
        require(
            msg.sender == admin || msg.sender == verifierContract,
            "Not authorized"
        );
        _;
    }

    constructor(address _deviceRegistry) {
        admin = msg.sender;
        deviceRegistry = DeviceRegistry(_deviceRegistry);

        // Set default alert thresholds
        alertThresholds["HIGH_HEART_RATE"] = 100;     // bpm
        alertThresholds["HIGH_TEMPERATURE"] = 40;      // celsius
        alertThresholds["LOW_TEMPERATURE"] = 0;        // celsius (adjusted to positive)
        alertThresholds["HIGH_HUMIDITY"] = 90;         // percentage
    }

    /**
     * @dev Set the verifier contract address (only admin)
     * @param _verifierContract Address of the ZKP verifier contract
     */
    function setVerifierContract(address _verifierContract) external onlyAdmin {
        require(_verifierContract != address(0), "Invalid verifier address");
        verifierContract = _verifierContract;
    }

    /**
     * @dev Log verified IoT data with ZKP proof
     * @param deviceId Device identifier
     * @param dataHash Hash of the sensor data
     * @param proofHash Hash of the ZKP proof
     * @param verified Whether the ZKP was successfully verified
     */
    function logData(
        bytes32 deviceId,
        bytes32 dataHash,
        bytes32 proofHash,
        bool verified
    ) external onlyAuthorized {
        require(deviceRegistry.isDeviceActive(deviceId), "Device not active");
        require(dataHash != bytes32(0), "Invalid data hash");
        require(proofHash != bytes32(0), "Invalid proof hash");
        require(!processedProofs[proofHash], "Proof already processed");

        LogEntry memory newLog = LogEntry({
            deviceId: deviceId,
            dataHash: dataHash,
            proofHash: proofHash,
            timestamp: block.timestamp,
            blockNumber: block.number,
            verified: verified
        });

        deviceLogs[deviceId].push(newLog);
        logCounts[deviceId]++;
        totalLogs++;
        processedProofs[proofHash] = true;

        emit DataLogged(deviceId, dataHash, proofHash, block.timestamp, verified);
    }

    /**
     * @dev Log batch of data with Merkle root and IPFS storage
     * @param merkleRoot Merkle root of the batched data
     * @param batchSize Number of items in the batch
     * @param ipfsHash IPFS hash where raw data is stored
     */
    function logBatch(
        bytes32 merkleRoot,
        uint256 batchSize,
        string calldata ipfsHash
    ) external onlyAuthorized {
        require(merkleRoot != bytes32(0), "Invalid merkle root");
        require(batchSize > 0, "Invalid batch size");
        require(bytes(ipfsHash).length > 0, "Invalid IPFS hash");

        BatchLog memory newBatch = BatchLog({
            merkleRoot: merkleRoot,
            batchSize: batchSize,
            timestamp: block.timestamp,
            ipfsHash: ipfsHash
        });

        batchLogs.push(newBatch);

        emit BatchLogged(merkleRoot, batchSize, ipfsHash, block.timestamp);
    }

    /**
     * @dev Trigger alert based on sensor data thresholds
     * @param deviceId Device identifier
     * @param alertType Type of alert (e.g., "HIGH_HEART_RATE")
     * @param dataHash Hash of the data that triggered the alert
     * @param value Sensor value that triggered the alert
     */
    function triggerAlert(
        bytes32 deviceId,
        string calldata alertType,
        bytes32 dataHash,
        uint256 value
    ) external onlyAuthorized {
        require(deviceRegistry.isDeviceActive(deviceId), "Device not active");
        require(value >= alertThresholds[alertType], "Value below threshold");

        emit AlertTriggered(deviceId, alertType, dataHash, block.timestamp);
    }

    /**
     * @dev Get device logs (paginated)
     * @param deviceId Device identifier
     * @param offset Starting index
     * @param limit Maximum number of results
     * @return Array of log entries
     */
    function getDeviceLogs(
        bytes32 deviceId,
        uint256 offset,
        uint256 limit
    ) external view returns (LogEntry[] memory) {
        LogEntry[] storage logs = deviceLogs[deviceId];
        require(offset < logs.length, "Offset out of bounds");

        uint256 end = offset + limit;
        if (end > logs.length) {
            end = logs.length;
        }

        LogEntry[] memory result = new LogEntry[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = logs[i];
        }

        return result;
    }

    /**
     * @dev Get recent logs across all devices (paginated)
     * @param limit Maximum number of results
     * @return Recent log entries
     */
    function getRecentLogs(uint256 limit) external view returns (LogEntry[] memory) {
        // This is a simplified implementation - in production, you'd want a more efficient approach
        // For now, we'll return the most recent logs from the first few devices
        LogEntry[] memory result = new LogEntry[](limit);
        uint256 count = 0;

        // This is not optimal but works for demo - in production use events indexing
        return result;
    }

    /**
     * @dev Get batch logs (paginated)
     * @param offset Starting index
     * @param limit Maximum number of results
     * @return Array of batch log entries
     */
    function getBatchLogs(
        uint256 offset,
        uint256 limit
    ) external view returns (BatchLog[] memory) {
        require(offset < batchLogs.length, "Offset out of bounds");

        uint256 end = offset + limit;
        if (end > batchLogs.length) {
            end = batchLogs.length;
        }

        BatchLog[] memory result = new BatchLog[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = batchLogs[i];
        }

        return result;
    }

    /**
     * @dev Update alert threshold (only admin)
     * @param alertType Type of alert
     * @param threshold New threshold value
     */
    function updateAlertThreshold(
        string calldata alertType,
        uint256 threshold
    ) external onlyAdmin {
        alertThresholds[alertType] = threshold;
    }

    /**
     * @dev Get statistics
     * @return totalLogs, totalBatches, totalDevices
     */
    function getStats() external view returns (uint256, uint256, uint256) {
        return (totalLogs, batchLogs.length, deviceRegistry.totalDevices());
    }
}

contracts/ZKPVerifier.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./DeviceRegistry.sol";
import "./IntegrityLog.sol";

/**
 * @title ZKPVerifier
 * @dev Verifies Zero-Knowledge Proofs for IoT device authentication
 * This contract will include the generated verifier from snarkjs
 */
contract ZKPVerifier {
    DeviceRegistry public deviceRegistry;
    IntegrityLog public integrityLog;

    // Events
    event ProofVerified(
        bytes32 indexed deviceId,
        address indexed prover,
        bool success,
        uint256 timestamp
    );

    event AuthenticationAttempt(
        bytes32 indexed deviceId,
        bool success,
        uint256 timestamp
    );

    // State variables
    mapping(bytes32 => uint256) public lastAuthTimestamp;
    mapping(bytes32 => uint256) public authAttemptCount;
    uint256 public constant AUTH_WINDOW = 300; // 5 minutes
    uint256 public constant MAX_AUTH_ATTEMPTS = 3;

    address public admin;

    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can perform this action");
        _;
    }

    constructor(address _deviceRegistry) {
        admin = msg.sender;
        deviceRegistry = DeviceRegistry(_deviceRegistry);
    }

    /**
     * @dev Set the IntegrityLog contract address
     * @param _integrityLog Address of the IntegrityLog contract
     */
    function setIntegrityLog(address _integrityLog) external onlyAdmin {
        integrityLog = IntegrityLog(_integrityLog);
    }

    /**
     * @dev Verify ZKP and authenticate device
     * @param deviceId Device identifier
     * @param _pA Proof component A
     * @param _pB Proof component B  
     * @param _pC Proof component C
     * @param publicSignals Public signals for the proof
     * @return bool Success status
     */
    function verifyAndAuthenticate(
        bytes32 deviceId,
        uint[2] calldata _pA,
        uint[2][2] calldata _pB,
        uint[2] calldata _pC,
        uint[4] calldata publicSignals
    ) external returns (bool) {
        // Check if device exists and is active
        require(deviceRegistry.isDeviceActive(deviceId), "Device not active");

        // Check rate limiting
        require(
            authAttemptCount[deviceId] < MAX_AUTH_ATTEMPTS ||
            block.timestamp > lastAuthTimestamp[deviceId] + AUTH_WINDOW,
            "Too many authentication attempts"
        );

        // Reset attempt count if window has passed
        if (block.timestamp > lastAuthTimestamp[deviceId] + AUTH_WINDOW) {
            authAttemptCount[deviceId] = 0;
        }

        authAttemptCount[deviceId]++;
        lastAuthTimestamp[deviceId] = block.timestamp;

        // Verify the proof (this will be replaced with actual snarkjs generated verifier)
        bool proofValid = _verifyProof(_pA, _pB, _pC, publicSignals);

        // Additional validation of public signals
        bool signalsValid = _validatePublicSignals(deviceId, publicSignals);

        bool success = proofValid && signalsValid;

        if (success) {
            // Reset attempt count on successful auth
            authAttemptCount[deviceId] = 0;

            // Log to IntegrityLog if available
            if (address(integrityLog) != address(0)) {
                bytes32 proofHash = _computeProofHash(_pA, _pB, _pC);
                bytes32 dataHash = keccak256(abi.encodePacked(deviceId, block.timestamp));
                integrityLog.logData(deviceId, dataHash, proofHash, true);
            }
        }

        emit ProofVerified(deviceId, msg.sender, success, block.timestamp);
        emit AuthenticationAttempt(deviceId, success, block.timestamp);

        return success;
    }

    /**
     * @dev Internal function to verify the proof (placeholder)
     * This will be replaced with the actual snarkjs generated verifier
     */
    function _verifyProof(
        uint[2] calldata _pA,
        uint[2][2] calldata _pB,
        uint[2] calldata _pC,
        uint[4] calldata publicSignals
    ) internal pure returns (bool) {
        // Placeholder - actual verification logic will be generated by snarkjs
        // For now, return true if proof components are non-zero
        return (
            _pA[0] != 0 && _pA[1] != 0 &&
            _pB[0][0] != 0 && _pB[0][1] != 0 &&
            _pB[1][0] != 0 && _pB[1][1] != 0 &&
            _pC[0] != 0 && _pC[1] != 0 &&
            publicSignals[0] != 0
        );
    }

    /**
     * @dev Validate public signals
     * @param deviceId Device identifier
     * @param publicSignals Array of public signals [secretHash, deviceId, timestamp, isValid]
     */
    function _validatePublicSignals(
        bytes32 deviceId,
        uint[4] calldata publicSignals
    ) internal view returns (bool) {
        // Extract public signals
        bytes32 secretHash = bytes32(publicSignals[0]);
        bytes32 signalDeviceId = bytes32(publicSignals[1]);
        uint256 timestamp = publicSignals[2];
        uint256 isValid = publicSignals[3];

        // Verify device ID matches
        if (signalDeviceId != deviceId) {
            return false;
        }

        // Verify timestamp is within acceptable window
        if (timestamp < block.timestamp - AUTH_WINDOW || timestamp > block.timestamp + 60) {
            return false;
        }

        // Verify secret hash matches registered hash
        bytes32 registeredHash = deviceRegistry.getSecretHash(deviceId);
        if (secretHash != registeredHash) {
            return false;
        }

        // Verify the proof claims to be valid
        if (isValid != 1) {
            return false;
        }

        return true;
    }

    /**
     * @dev Compute hash of proof components for logging
     */
    function _computeProofHash(
        uint[2] calldata _pA,
        uint[2][2] calldata _pB,
        uint[2] calldata _pC
    ) internal pure returns (bytes32) {
        return keccak256(abi.encodePacked(_pA, _pB, _pC));
    }

    /**
     * @dev Get authentication statistics for a device
     * @param deviceId Device identifier
     * @return lastAuth, attemptCount, isRateLimited
     */
    function getAuthStats(bytes32 deviceId) 
        external 
        view 
        returns (uint256, uint256, bool) 
    {
        bool isRateLimited = (
            authAttemptCount[deviceId] >= MAX_AUTH_ATTEMPTS &&
            block.timestamp <= lastAuthTimestamp[deviceId] + AUTH_WINDOW
        );

        return (
            lastAuthTimestamp[deviceId],
            authAttemptCount[deviceId],
            isRateLimited
        );
    }

    /**
     * @dev Admin function to reset auth attempts for a device
     * @param deviceId Device identifier
     */
    function resetAuthAttempts(bytes32 deviceId) external onlyAdmin {
        authAttemptCount[deviceId] = 0;
        lastAuthTimestamp[deviceId] = 0;
    }
}

